mapper.select:
select noti_num,
noti_title, noti_date
from (select rownum rank, noti_num, noti_title,
noti_date
from noti_tb)
order by rank desc

mapper.update:
update faq_tb
set faq_title = #{faq_title},
faq_content = #{faq_content}, faq_category = #{faq_category}, faq_file
= #{faq_file}
where faq_num = #{faq_num}
